#!/bin/bash# Start SSH serviceservice ssh start# Check if NameNode directory is empty and format if necessary# This prevents re-formatting on subsequent container restarts if data persists via volumesif [! -d "/opt/hadoop_data/namenode/current" ]; then  echo "Formatting NameNode..."  $HADOOP_HOME/bin/hdfs namenode -format -forceelse  echo "NameNode already formatted. Skipping format."fi# Start HDFS services (NameNode, DataNodes)echo "Starting HDFS services..."$HADOOP_HOME/sbin/start-dfs.sh# Start YARN services (ResourceManager, NodeManagers)echo "Starting YARN services..."$HADOOP_HOME/sbin/start-yarn.sh# Keep the container runningtail -f /dev/null